{
  "title": "Slider Parameters (per Shopware configurations)",
  "type": "object",
  "properties": {
    "displayMode": {
      "title": "Display Mode",
      "type": "string",
      "enum": [
        "standard",
        "cover",
        "contain"
      ]
    },
    "verticalAlign": {
      "title": "Vertical Align",
      "type": "string",
      "enum": [
        "",
        "flext-start",
        "center",
        "flex-end"
      ]
    },
    "boxLayout": {
      "title": "Layout Type",
      "type": "string",
      "enum": [
        "standard",
        "image",
        "minimal"
      ]
    },
    "navigation": {
      "title": "Navigation",
      "type": "boolean",
      "format": "checkbox"
    },
    "rotate": {
      "title": "Automatic sliding",
      "type": "boolean",
      "format": "checkbox"
    },
    "border": {
      "title": "Border",
      "type": "boolean",
      "format": "checkbox"
    },
    "elMinWidth": {
      "title": "Element Minimal Width",
      "type": "string",
      "description": "ex: 150px"
    },
    "title": {
      "type": "object",
      "title": "Title",
      "properties": {
        "de": {
          "type": "string",
          "description": "Slider Title in German"
        },
        "en": {
          "type": "string",
          "description": "Slider Title in English"
        }
      }
    },
    "accessor": {
      "type": "string",
      "description": "hits accessor (typically leave empty)"
    }
  }
}

